package Assignment02;

public class SpecialCharEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println('\'');				//\' 문자 ' 출력
		System.out.println("abc\t123\b456");	//\t 탭 \b 백스페이스 - abc	12456으로 출력되어야되지 않나?
		System.out.println('\n');				//\n 엔터한줄
		System.out.println("\"Hello\"");		//\" 사용해서 "출력
		System.out.println("c:\\");				//\\이 \가 되어 하나만 출력
	}

}
