package Examples;

public class InputOutput01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String myName = "홍길동";		//문자열 String
		int myAge = 20;				//초기값 20으로 설정
		
		System.out.println("이름 : " + myName);
		System.out.println("나이 : " + myAge);
		
	}

}
