package Examples;

public class InputOutput02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String myName = "홍길동";		//문자열 String
		int myAge = 20; 			//초기값 20으로 설정
		
		System.out.print("이름 : \t" + myName + "\n"); //탭, 줄바꿈
		System.out.print("나이 : \t" + myAge + "\n");
	}

}
