package Homework0411;

public class Example3_8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double d = 85.4;	//실수형
		int score = (int)d;		//형변환. double->int 85.4->85
		
		System.out.println("score=" + score);	//score은 int형 변수. 85 출력
		System.out.println("d=" + d);	//d는 double형 변수. 85.4 출력
	}

}
