package Homework0411;

public class Operator02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10, b = 25;
		System.out.println("a == b = " + (a == b));	//같으면 참
		System.out.println("a != b = " + (a != b));	//같지않으면 참
		System.out.println("a > b = " + (a > b));	//a가 b보다 크면 참
		System.out.println("a < b = " + (a < b));	//b가 a보다 크면 참
		System.out.println("b >= a = " + (b >= a));	//b가 a이상이면 참
		System.out.println("b <= a = " + (b <= a));	//a가 b이상이면 참
	
	}

}
