package Homework0411;

public class Operator04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		int b = 20;
		int c;
		System.out.println(c = a);		//a값을 c에 대입
		System.out.println(b += a);		//b = b + a
		System.out.println(b -= a);		//b = b - a
		System.out.println(b *= a);		//b = b * a
		System.out.println(b /= a);		//b = b / a
		System.out.println(b %= a);		//b = b % a
	}

}
