package Homework0411;

public class Operator05 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10, b = 10, c = 10, d = 10;
		
		System.out.println("a++ => " + (a++));	//출력 후 증가	(10)
		System.out.println("a => " + a);		//위에서 증가한상태(11)
		System.out.println("++b => " + (++b));	//증가 후 출력 (11)
		System.out.println("c-- => " + (c--));	//출력 후 감소	(10)
		System.out.println("--d => " + (--d));	//감소 후 출력 (9)
		
	}

}
