package Homework0411;

public class TypeConversion01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 10;
		double y = 3.0;
		System.out.println(x + y);	//double 실수형으로 출력
		System.out.println(x - y);	//double 실수형으로 출력
		System.out.println(x * y);	//double 실수형으로 출력
		System.out.println(x / y);	//double 실수형으로 출력
		
	}

}
