package Homework0412;

public class HW4_8_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i = 1; i <= 10; i++) {
		    for(int j = 1; j <= i; j++) {
		        System.out.print("*");	//j<=i일때 j만큼 "*"출력
		    }
		    System.out.println(); //i 증가할 때 줄바꿈
		}		
	}
}

