package Homework0412;

public class HW4_9 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int sum = 0;	//sum 초기화
		for(int i = 0; i < 10; i++) {	//i 초기화 조건 증감
			sum = sum + i;	//i가 0 부터 9까지 i값의 합을 나타냄			
		}
		System.out.println("sum: " + sum);		
	}
}
