package chap03;

public class Example01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 10, y = 20;
		System.out.println("x + y = " + (x + y)); //연산시킬 때 ()로 묶어줌 - 식별 용이함.
		System.out.println("x - y = " + (x - y));
		System.out.println("x * y = " + (x * y));
		System.out.println("x / y = " + (x / y));
		System.out.println("x % y = " + (x % y));
		
	}

}
