package chap03;

public class Example05 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 10;
		System.out.println(x++);	//출력 후 증가(10)
		System.out.println(++x);	//증가 후 출력(12)
		System.out.println(x--);	//출력 후 감소(12)
		System.out.println(--x);	//감소 후 출력(10)
		
	}

}
