package chap03;

public class Example07 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double d = 3.14;
		long l = (long)d;
		int i = (int)l;
		System.out.println(d);	//double형
		System.out.println(l);	//long형
		System.out.println(i);	//int형
		
	}

}
