package chap04;

public class Example04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 70;	//초기값 70
		if(num < 100) {		//100 미만일 때 작동
			System.out.println("숫자가 100보다 작습니다.");
			if(num > 50) {	//50 초과일 때 작동
				System.out.println("숫자가 50보다 큽니다.");
				
			}
		}
	}

}
